CREATE SCHEMA "public";

CREATE SEQUENCE actor_id_seq START WITH 1;

CREATE SEQUENCE actor_id_seq1 START WITH 1;

CREATE SEQUENCE character_id_seq START WITH 1;

CREATE SEQUENCE dialogue_id_seq START WITH 1;

CREATE SEQUENCE film_id_seq START WITH 1;

CREATE SEQUENCE filmset_id_seq START WITH 1;

CREATE SEQUENCE planning_id_seq START WITH 1;

CREATE SEQUENCE scene_id_seq START WITH 1;

CREATE SEQUENCE filmset_unavailable_id_seq START WITH 1;

CREATE SEQUENCE holiday_id_seq START WITH 1;

CREATE  TABLE film ( 
	id                   integer DEFAULT nextval('film_id_seq'::regclass) NOT NULL  ,
	title                varchar(60)  NOT NULL ,
	description          text  NOT NULL ,
	duration             time DEFAULT '01:00:00'::time without time zone NOT NULL ,
	nbr_team             integer DEFAULT 1 NOT NULL ,
	visuel               text   ,
	CONSTRAINT pk_film_id PRIMARY KEY ( id )
 );

CREATE  TABLE gender ( 
	id                   integer  NOT NULL ,
	name                 varchar(100)  NOT NULL ,
	CONSTRAINT pk_gender_id PRIMARY KEY ( id )
 );

CREATE  TABLE settings ( 
	id                   integer  NOT NULL ,
	name                 varchar(100)  NOT NULL ,
	"value"              float8  NOT NULL ,
	CONSTRAINT pk_settings_id PRIMARY KEY ( id )
 );

CREATE  TABLE status_planning ( 
	id                   integer  NOT NULL ,
	name                 varchar(50)  NOT NULL ,
	CONSTRAINT pk_status_planning_id PRIMARY KEY ( id )
 );

CREATE  TABLE type_filmset ( 
	id                   integer  NOT NULL ,
	name                 varchar(50)  NOT NULL ,
	CONSTRAINT pk_type_filmset_id PRIMARY KEY ( id )
 );

CREATE  TABLE actor ( 
	id                   integer GENERATED BY DEFAULT AS IDENTITY  NOT NULL ,
	name                 varchar(100)  NOT NULL ,
	birthdate            date  NOT NULL ,
	contact              varchar(100)  NOT NULL ,
	gender               integer  NOT NULL ,
	CONSTRAINT pk_personnage_id PRIMARY KEY ( id ),
	CONSTRAINT fk_actor_gender FOREIGN KEY ( gender ) REFERENCES gender( id )  
 );

CREATE  TABLE "character" ( 
	id                   integer DEFAULT nextval('character_id_seq'::regclass) NOT NULL  ,
	name                 varchar(100)  NOT NULL ,
	description          text  NOT NULL ,
	gender               integer  NOT NULL ,
	film_id              integer  NOT NULL ,
	actor_id             integer  NOT NULL ,
	CONSTRAINT pk_p_id PRIMARY KEY ( id ),
	CONSTRAINT idx_p UNIQUE ( name ) ,
	CONSTRAINT fk_character_actor FOREIGN KEY ( actor_id ) REFERENCES actor( id )  ,
	CONSTRAINT fk_character_film FOREIGN KEY ( film_id ) REFERENCES film( id )  ,
	CONSTRAINT fk_character_gender FOREIGN KEY ( gender ) REFERENCES gender( id )  
 );

CREATE  TABLE filmset ( 
	id                   integer DEFAULT nextval('filmset_id_seq'::regclass) NOT NULL  ,
	name                 varchar(100)  NOT NULL ,
	type_id              integer  NOT NULL ,
	x                    double precision  NOT NULL ,
	y                    double precision  NOT NULL ,
	CONSTRAINT pk_filmset_id PRIMARY KEY ( id ),
	CONSTRAINT fk_filmset_type_filmset FOREIGN KEY ( type_id ) REFERENCES type_filmset( id )  
 );

CREATE  TABLE filmset_unavailable ( 
	id                   integer DEFAULT nextval('filmset_unavailable_id_seq'::regclass) NOT NULL  ,
	date_debut            DATE NOT NULL DEFAULT current_date ,
	date_fin            DATE NOT NULL,
	filmset_id           integer  NOT NULL ,
	observation VARCHAR(255),
	CONSTRAINT pk_filmset_planning_id PRIMARY KEY ( id ),
	CONSTRAINT fk_filmset_planning_filmset FOREIGN KEY ( filmset_id ) REFERENCES filmset( id )  
 );

CREATE  TABLE scene ( 
	id                   integer DEFAULT nextval('scene_id_seq'::regclass) NOT NULL  ,
	title                varchar(100)  NOT NULL ,
	global_action        text  NOT NULL ,
	time_start           time  NOT NULL ,
	time_end             time  NOT NULL ,
	estimated_time       time  NOT NULL ,
	filmset_id           integer  NOT NULL ,
	film_id              integer  NOT NULL ,
     preferred_shooting_time TIME,
	status INTEGER NOT NULL DEFAULT 1,
	CONSTRAINT pk_scenery_id PRIMARY KEY ( id ),
	CONSTRAINT fk_scene_film FOREIGN KEY ( film_id ) REFERENCES film( id )  ,
	CONSTRAINT fk_scene_filmset FOREIGN KEY ( filmset_id ) REFERENCES filmset( id )  
 );

CREATE  TABLE dialogue ( 
	id                   integer DEFAULT nextval('dialogue_id_seq'::regclass) NOT NULL  ,
	scene_id             integer  NOT NULL ,
	character_id         integer  NOT NULL ,
	texte                text  NOT NULL ,
	"action"             text   ,
	CONSTRAINT pk_dialogue_id PRIMARY KEY ( id ),
	CONSTRAINT fk_dialogue_character FOREIGN KEY ( character_id ) REFERENCES "character"( id )  ,
	CONSTRAINT fk_dialogue_scene FOREIGN KEY ( scene_id ) REFERENCES scene( id )  
 );

CREATE  TABLE planning ( 
	id                   integer DEFAULT nextval('planning_id_seq'::regclass) NOT NULL  ,
	scene_id             integer  NOT NULL ,
	date_debut           timestamp  NOT NULL ,
	date_fin             timestamp  NOT NULL ,
	CONSTRAINT pk_planning_id PRIMARY KEY ( id ),
	CONSTRAINT fk_planning_scene FOREIGN KEY ( scene_id ) REFERENCES scene( id )
 );

CREATE TABLE holiday(
	id                   integer DEFAULT nextval('holiday_id_seq'::regclass) NOT NULL  ,
	date            DATE NOT NULL,
	name VARCHAR(255),
	CONSTRAINT pk_holiday_id PRIMARY KEY ( id )
);
