--view
CREATE OR REPLACE VIEW scene_status AS (
    SELECT
    Scene.*
    FROM scene
    WHERE Scene.id not in (select scene_id FROM planning)
    UNION    
    SELECT DISTINCT
    Scene.*
    FROM Scene
    join Planning on
    Scene.id = Planning.scene_id
);
